mapper.select:
SELECT USER_IDX
FROM CHAT_USER
WHERE ROOM_IDX = #{userIdx}

SELECT ROOM_IDX, ROOM_NAME
FROM CHAT_ROOM
WHERE ROOM_IDX = #{roomIdx}

mapper.delete:
UPDATE CHAT_USER
SET USER_STATUS = 0
WHERE USER_IDX = #{userIdx}
  AND ROOM_IDX = #{roomIdx}

UPDATE CHAT_ROOM
SET ROOM_STATUS = 0
WHERE ROOM_IDX = #{roomIdx}
